mapper.insert:
INSERT INTO MP_BOARD(   BNO
					      ,	TITLE 
					      , CONTENT 
					      , WRITER  )
              VALUES(   #{bno}
              		  ,	#{title}
              		  , #{content}
              		  , #{writer}  )

mapper.select:
select * from 
	(select @rownum:=@rownum+1 R, A.* from (select * from mp_board order by bno desc) A , (SELECT @rownum:=0) tmp) B 
    where R between #{rowStart} and #{rowEnd}

mapper.update:
UPDATE MP_BOARD
   SET TITLE    =   #{title},
   	   CONTENT  =   #{content}
 WHERE BNO = #{bno}
